    //     <h1>Hellow</h1>
    //     <h1>Hellow</h1>
    //   </div>
    // );

    // const items = () => {
    //   {
    //     this.state.json.map(carousel => [
    //       <div key={1}>
    //         <img src={require(`../images/${carousel}.jpg`)} alt="banner" />
