import React, { Component } from "react";

import { Typography, Container, Paper, Grid } from "@material-ui/core";
import OwlCarousel from "react-owl-carousel2";
import "react-owl-carousel2/lib/styles.css";
import Slider from "react-slick";

const Materi = props => {
  return (
    <React.Fragment>
      <Container>
        <img src={props.img} alt="image-slidder" />
      </Container>
      <Typography variant="p">{props.desc}</Typography>
      <Typography variant="p">{props.harga}</Typography>
    </React.Fragment>
  );
};

export class Kursus extends Component {
  constructor(props) {
    super(props);
    this.state = {
      paket: "kursus programming",
      materi: "react js",
      desc: "belajar react js",
      harga: 200000
    };
  }
  render() {
    return (
      <Container>
        <Typography variant="h2">Nama Paket : {this.state.paket}</Typography>
        <Typography variant="h2">Jenis Materi : {this.state.materi}</Typography>
        <Typography variant="h2">
          Deskripsi Paket : {this.state.desc}
        </Typography>
        <Typography variant="h2">Harga Paket : {this.state.harga}</Typography>
      </Container>
    );
  }
}

class Courses extends Component {
  render() {
    return (
      <Container>
        <Typography component="h1" variant="h1">
          Contoh State
        </Typography>
        <hr />
        <Kursus />
        <Materi
          img={require("../images/image1.jpg")}
          desc="belajar SEO"
          harga="120000"
        />
        <Materi
          img={require("../images/image2.jpg")}
          desc="belajar google adword"
          harga="400000"
        />
        <Materi
          img={require("../images/image3.jpg")}
          desc="belajar react js"
          harga="500000"
        />
      </Container>
    );
  }
}

export default Courses;
