import React, { Component } from "react";
import { NavLink } from "react-router-dom";

import { Course } from "./Courses";
import { Toolbar, Button, Typography, Container } from "@material-ui/core";
import "react-owl-carousel2/lib/styles.css";

const CourseDetail = props => {
  return (
    <Container
      style={{
        display: "flex",
        borderRadius: "25px",
        flexWrap: "wrap",
        backgroundColor: "#F1F0FF",
        height: "100%",
        padding: 0,
        marginTop: 20
      }}
    >
      <Container
        style={{
          width: "40%",
          float: "left",
          margin: 0,
          marginLeft: 10
        }}
      >
        <Course
          img={props.img}
          title={props.title}
          author={props.author}
          lessons={props.lessons}
          button={props.button}
          buttonColor={props.buttonColor}
          price={props.price}
          backgroundColor="white"
        />
      </Container>
      <Container
        style={{
          margin: 0,
          paddingTop: 20,
          width: "50%"
        }}
      >
        <Typography
          component="h3"
          variant="h4"
          style={{ marginBottom: 30, fontWeight: 700 }}
        >
          Author : {props.author}
        </Typography>
        <Typography
          component="h2"
          variant="h2"
          style={{ color: "#EC9120", marginBottom: 30 }}
        >
          {props.title}
        </Typography>
        <Typography
          component="h3"
          variant="h5"
          style={{ marginBottom: 30, fontWeight: 700 }}
        >
          {props.lessons}
        </Typography>
        <Typography
          component="h3"
          variant="p"
          style={{
            lineHeight: 1.5,
            fontSize: "20px",
            fontWeight: 400,
            marginBottom: 30
          }}
        >
          {props.desc}
        </Typography>
      </Container>
    </Container>
  );
};

class CoursesDetail extends Component {
  render() {
    return (
      <Container
        style={{
          minHeight: "600px",
          margin: "0 auto",
          padding: 0,
          marginTop: "100px"
        }}
      >
        <Container
          fixed
          style={{
            margin: "20px",
            paddingTop: 20,
            height: "100%",
            borderRadius: "25px"
          }}
        >
          <Typography
            component="h1"
            align="center"
            variant="h1"
            style={{ textAlign: "center", color: "#EC9120" }}
          >
            Detail Paket Kursus
          </Typography>
          <Container
            fixed
            style={{
              height: "90%",
              marginTop: 20,
              width: "100%"
            }}
          >
            <CourseDetail
              img={require("../images/image3.jpg")}
              title="Catalytic Conversions"
              author="Svyatoslav Taushev"
              lessons="10 Lessons"
              button="medicine"
              buttonColor="#603DBB"
              price="Rp 7,5JT"
              desc="
              Babastudio.com (Web Consultant & Training) telah berdiri lebih dari 10
              tahun sejak tahun 2003 dan telah dilengkapi badan hukum.
              Babastudio.com dalam pengalamannya telah merebut 5 penghargaan ICT
              Awards (Piala MENKOMINFO) dalam bidang website, sebagai website
              korporasi terbaik dan juga dalam kategory People Choice.
            "
            />
          </Container>
        </Container>
      </Container>
    );
  }
}

export default CoursesDetail;
