import React, { Component } from "react";

import { Typography, Container, Paper, Grid } from "@material-ui/core";
import OwlCarousel from "react-owl-carousel2";
import "react-owl-carousel2/lib/styles.css";

import Slider from "react-slick";

class Blog extends Component {
  render() {
    return (
      <Container
        style={{
          marginTop: 80,
          height: "600px"
        }}
      >
        <Container
          style={{
            borderTopLeftRadius: "20px",
            borderBottomLeftRadius: "20px",
            backgroundColor: "#F1F0FF",
            width: "80%",
            height: "600px",
            right: 0,
            position: "absolute"
          }}
        >
          <Grid item xs={11} md={11}>
            <img
              style={{
                width: "500px",
                position: "absolute",
                top: "50%",
                height: "500px",
                left: -200,
                transform: "translateY(-50%)",
                borderRadius: "5px",
                border: "1px solid whitesmoke",
              }}
              src={require("../images/republika.jpg")}
              alt="slider-image"
            />
          </Grid>
        </Container>
        <Container
          style={{
            width: "50%",
            height: "400px",
            right: 80,
            position: "absolute",
            paddingTop: "60px",
            paddingRight: "100px"
          }}
        >
          <Typography
            component="h3"
            variant="h4"
            style={{ marginBottom: 40, fontWeight: 700 }}
          >About us</Typography>
          <Typography
            component="h2"
            variant="h3"
            style={{ color: "#EC9120", marginBottom: 40 }}
          >We Are Experts
          in Elearning</Typography>
          <Typography component="h3" variant="h5" style={{ marginBottom: 40 }}>Kenapa memilih kami</Typography>
          <Typography component="h3" variant="p" style={{ lineHeight: 1.5, marginBottom: 40 }}>Babastudio.com (Web Consultant & Training) telah berdiri lebih dari 10 tahun sejak tahun 2003 dan telah dilengkapi badan hukum. Babastudio.com dalam pengalamannya telah merebut 5 penghargaan ICT Awards (Piala MENKOMINFO) dalam bidang website, sebagai website korporasi terbaik dan juga dalam kategory People Choice. </Typography>
        </Container>
      </Container>
      /* Testimony end */
    );
  }
}

export default Blog;
