import React, { Component } from "react";

import { Button, Typography, Container, Paper, Grid } from "@material-ui/core";
import "react-owl-carousel2/lib/styles.css";

const Materi = props => {
  return (
    <Container
      style={{
        flex: "1 0 25%",
        maxWidth: "364px",
        maxHeight: "650px",
        padding: 0,
        backgroundColor: "white",
        margin: "20px 10px",
        borderRadius: "24px",
        textAlign: "center",
        position: "relative"
      }}
    >
      <img style={{ 
        borderTopLeftRadius: "24px",
        borderTopRightRadius: "24px",
        width: "100%" }} src={props.img} alt="image-slidder" />
        <Button
                  style={{
                    backgroundColor: "lightblue",
                    fontWeight: "900",
                    borderRadius: "24px",
                    color: "white",
                    marginBottom: "10px",
                    position: "absolute",
                    top: 20,
                    left: 20
                  }}
                  href="#"
                  variant="contained"
                >
                  {props.button}
                </Button>
      <Typography style={{color: "#EC9120"}} variant="h4">{props.desc}</Typography>
      <Typography variant="h5">{props.desc}</Typography>
      <Typography variant="body2">{props.harga}</Typography>
    </Container>
  );
};

class Courses extends Component {
  render() {
    return (
      <Container
        style={{
          minHeight: "600px",
          margin: 0,
          padding: 0,
          marginTop: "100px"
        }}
      >
        <Container
          fixed
          style={{
            margin: "20px",
            paddingTop: 20,
            height: "100%",
            borderRadius: "25px",
            backgroundColor: "#F1F0FF"
          }}
        >
          <Typography
            component="h3"
            variant="h4"
            style={{ marginBottom: 10, fontWeight: 700, textAlign: "center" }}
          >
            Schedules
          </Typography>
          <Typography
            component="h1"
            align="center"
            variant="h3"
            style={{ textAlign: "center", color: "#EC9120" }}
          >
            Upcoming Events
          </Typography>
          <Container
            fixed
            style={{ height: "90%", marginTop: 20, width: "100%" }}
          >
            <Container
              style={{
                display: "flex",
                borderRadius: "25px",
                flexWrap: "wrap",
                height: "100%",
                padding: 0,
                marginTop: 20
              }}
            >
              <Materi
                img={require("../images/image1.jpg")}
                desc="belajar SEO"
                harga="120000"
                button="website"
              />
            </Container>
          </Container>
        </Container>
      </Container>
    );
  }
}

export default Courses;
