import React, { Component } from "react";
import { NavLink } from "react-router-dom";

import { Toolbar, Button, Typography, Container } from "@material-ui/core";
import "react-owl-carousel2/lib/styles.css";

export const NewsProps = props => {
  return (
    <Container
      style={{
        padding: 0,
        margin: "20px 10px",
        borderRadius: "24px",
        textAlign: "center",
        marginBottom: "150px"
      }}
    >
      <NavLink
        style={{
          display: "inline-block",
          position: "relative",
          textDecoration: "none"
        }}
        to="/news-detail"
        exact
      >
        <Container
          style={{
            position: "relative",
            margin: 0,
            padding: 0
          }}
        >
          <img
            style={{
              borderRadius: "24px",
              width: "100%",
              height: props.imgHeight
            }}
            src={props.img}
            alt="image-slidder"
          />
          <Button
            style={{
              backgroundColor: props.buttonColor,
              fontWeight: "900",
              borderRadius: "24px",
              color: "white",
              marginBottom: "10px",
              position: "absolute",
              top: 20,
              left: 20
            }}
            href="#"
            variant="contained"
          >
            {props.button}
          </Button>
        </Container>
        <Container
          style={{
            minHeight: "150px",
            backgroundColor: props.backgroundColor,
            width: "80%",
            position: "absolute",
            bottom: -100,
            left: "50%",
            transform: "translateX(-50%)",
            border: "1px solid gray",
            borderRadius: "5px",
            padding: 8
          }}
        >
          <Typography style={{ color: "#EC9120" }} variant="h4">
            {props.title}
          </Typography>
          <Typography style={{ marginTop: "10px" }} variant="h5">
            {props.author}
          </Typography>
        </Container>
      </NavLink>
    </Container>
  );
};

export class News extends Component {
