              <Typography variant="h5">SEO</Typography>
            </NavLink>
            <NavLink
              style={{
                display: "inline-block",
                textDecoration: "none"
              }}
              to="/contact"
            >
              <Typography variant="h5">Marketing</Typography>
            </NavLink>
          </Toolbar>
          <Container
            fixed
            style={{ height: "90%", marginTop: 20, width: "100%" }}
          >
            <Container
              style={{
                display: "flex",
                borderRadius: "25px",
                flexWrap: "wrap",
                height: "100%",
                padding: 0,
