import React, { Component } from "react";
import { NavLink } from "react-router-dom";

import { Toolbar, Button, Typography, Container } from "@material-ui/core";
import "react-owl-carousel2/lib/styles.css";

export const Course = props => {
  return (
    <Container
      style={{
        flex: "1 0 25%",
        maxWidth: "364px",
        maxHeight: "750px",
        padding: 0,
        backgroundColor: (props.backgroundColor),
        margin: "20px 10px",
        borderRadius: "24px",
        textAlign: "center"
      }}
    >
      <NavLink
        style={{
          display: "inline-block",
          textDecoration: "none"
        }}
        to="/courses-detail"
        exact
      >
        <Container
          style={{
            position: "relative",
            minHeight: "300px",
            margin: 0,
            padding: 0
          }}
        >
          <img
            style={{
              borderTopLeftRadius: "24px",
              borderTopRightRadius: "24px",
              width: "100%",
              height: "100%"
            }}
            src={props.img}
            alt="image-slidder"
          />
          <Button
            style={{
              backgroundColor: props.buttonColor,
              fontWeight: "900",
              borderRadius: "24px",
              color: "white",
              marginBottom: "10px",
              position: "absolute",
              top: 20,
              left: 20
            }}
            href="#"
            variant="contained"
          >
            {props.button}
          </Button>
        </Container>
        <Container
          style={{
            minHeight: "150px",
            margin: 0,
            paddingLeft: 0,
            paddingRight: 0
          }}
        >
          <Typography style={{ color: "#EC9120" }} variant="h4">
            {props.title}
          </Typography>
          <Typography style={{ marginTop: "10px" }} variant="h5">
            {props.author}
          </Typography>
          <Typography style={{ marginTop: "10px" }} variant="body2">
            {props.lessons}
          </Typography>
          <Typography
            style={{ color: "grey", margin: "10px", textAlign: "right" }}
            variant="h5"
          >
            {props.price}
          </Typography>
        </Container>
      </NavLink>
    </Container>
  );
};

export class Courses extends Component {
  render() {
    return (
      <Container
        style={{
          minHeight: "600px",
          margin: "0 auto",
          padding: 0,
          marginTop: "100px"
