        <Container
          fixed
          style={{
            margin: "20px",
            paddingTop: 20,
            height: "100%",
            borderRadius: "25px"
          }}
        >
          <Typography
            component="h1"
            align="center"
            variant="h1"
            style={{ textAlign: "center", color: "#EC9120" }}
          >
            Pilihan Paket Materi
          </Typography>
          <Toolbar
            style={{
              margin: 40,
              borderRadius: "24px",
              display: "flex",
              backgroundColor: "#F1F0FF",
              justifyContent: "space-around"
            }}
          >
            <NavLink
              style={{
                display: "inline-block",
                textDecoration: "none"
              }}
              to="/"
              exact
            >
              <Typography variant="h5">All</Typography>
            </NavLink>
            <NavLink
              style={{
                display: "inline-block",
                textDecoration: "none"
              }}
              to="/about-us"
            >
              <Typography variant="h5">Website</Typography>
            </NavLink>
            <NavLink
              style={{
                display: "inline-block",
                textDecoration: "none"
              }}
              to="/courses"
            >
              <Typography variant="h5">Mobile</Typography>
            </NavLink>
            <NavLink
              style={{
                display: "inline-block",
                textDecoration: "none"
              }}
              to="/news"
            >
              <Typography variant="h5">Design</Typography>
            </NavLink>
            <NavLink
              style={{
                display: "inline-block",
                textDecoration: "none"
              }}
              to="/gallery"
            >
              <Typography variant="h5">Office</Typography>
            </NavLink>
            <NavLink
              style={{
                display: "inline-block",
                textDecoration: "none"
              }}
              to="/contact"
            >
              <Typography variant="h5">SEO</Typography>
            </NavLink>
            <NavLink
              style={{
                display: "inline-block",
                textDecoration: "none"
              }}
              to="/contact"
            >
              <Typography variant="h5">Marketing</Typography>
            </NavLink>
          </Toolbar>
          <Container
            fixed
            style={{ height: "90%", marginTop: 20, width: "100%" }}
          >
            <Container
              style={{
                display: "flex",
                borderRadius: "25px",
                flexWrap: "wrap",
                height: "100%",
                padding: 0,
                marginTop: 20
              }}
            >
              <Course
                img={require("../images/fe.jpg")}
