import React, { Component } from "react";

import { Typography, Container, Paper, Grid } from "@material-ui/core";
import OwlCarousel from "react-owl-carousel2";
import "react-owl-carousel2/lib/styles.css";
import Slider from "react-slick";

const Materi = (props) => {
  return (
      <div className="materi wrapper">
          <div className="content">
              <div className="img-container">
                  <img src={props.img}  alt="image-slidder" className="image-slidder"/>
              </div>
              <p className="desc">{props.desc}</p>
              <p className="harga">{props.harga}</p>
          </div>
      </div>
  );
}

export class Kursus extends Component {
  constructor(props) {
      super(props);
      this.state = {
          paket: "kursus programming",
          materi: "react js",
          desc: "belajar react js",
          harga: 200000
      };
  }
  render() {
      return (
          <div className="state wrapper">
              <h2>Nama Paket      : {this.state.paket}</h2><br/>
              <h2>Jenis Materi      : {this.state.materi}</h2><br/>
              <h2>Deskripsi Paket      : {this.state.desc}</h2><br/>
              <h2>Harga Paket      : {this.state.harga}</h2><br/>
          </div>
      );
  }
}

class Courses extends Component {
  render() {
      return (
        <div className="App">
          <h1>Contoh State</h1>
          <hr/>
          <Kursus/>
          <Materi img={require("./props/images/image1.jpg")} desc="belajar SEO" harga="120000"/>
          <Materi img={require("./props/images/image2.jpg")} desc="belajar google adword" harga="400000"/>
          <Materi img={require("./props/images/image3.jpg")} desc="belajar react js" harga="500000"/>
        </div>
      );
  }
}

export default Courses;
